package org.cg.StringInbuiltMethod;

public class Concat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str="tippa";
		String str1=" reddy";
		String str2=" k";
		System.out.println(str.concat(str1.concat(str2)));
		//using plus
		String a="hey";
		String b=" my name is reddy";
		int c=10;
		int d=5;
		float e=1.256f;
		System.out.println(c+d+e);
		
		System.out.println(a+b+c);

	}

}
