package org.cg.StringInbuiltMethod;

public class StartsAndEndsWith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="happi birthday pspk";
		System.out.println(s1.startsWith("h"));
		System.out.println(s1.startsWith("a"));
		System.out.println(s1.startsWith("a",1));

		String s="happy birthday";
		System.out.println(s.endsWith("eey"));
		System.out.println(s.endsWith("happy"));
		System.out.println(s.endsWith("g"));

	}


	}
