package org.cg.StringInbuiltMethod;

public class replaceDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="tippa redddy";
		System.out.println(s.replace('a', 'd'));
		System.out.println(s.replace('d', 'e'));
		String s1="negative Minds";
		System.out.println(s1.replace("negative","positive"));
		String str = (s1.replace(s,s1));
		System.out.println(str);
		

	}

}
