package org.cg.StringInbuiltMethod;

public class trim {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="tippa";
		String s1="             r e ddy     ";
		System.out.println(s1);
		System.out.println(s1.trim());
		System.out.println(s+s1.trim());

	}

}
